mapper.insert:
INSERT INTO tbl_tradinglist
(
username nVARCHAR(100) primary key,
post_title VARCHAR(100) not null,
post_situ VARCHAR(100) not null
) VALUES (
#{username},
#{post_title},
#{post_situ}
)

mapper.update:
UPDATE tbl_tradinglist SET
post_title =
#{post_title},
post_situ = #{post_situ}
WHERE username =
#{username}

mapper.select:
CREATE TABLE tbl_tradinglist (
username nVARCHAR(100) primary key,
post_title VARCHAR(100) not null,
post_situ VARCHAR(100) not null
);
